import { Tx } from "../../ch05/Tx";

test("isCoinbase", () => {
  const rawTx = Buffer.from(
    "",
    "hex"
  );
  const tx = Tx.parse(rawTx);
  expect(tx.isCoinbase()).toBe(true);
});

test("coinbaseHeight", () => {
  let rawTx = Buffer.from(
    "",
    "hex"
  );
  let tx = Tx.parse(rawTx);
  expect(tx.coinbaseHeight()).toBe(465879);
  rawTx = Buffer.from(
    "",
    "hex"
  );
  tx = Tx.parse(rawTx);
  expect(() => tx.coinbaseHeight()).toThrow();
});
